import React from "react";
import styled, { css } from "styled-components";

export interface AbBotaoProps {
    texto?: string
    tipo?: 'primario' | 'secundario'
    onClick?: () => void
}

const BotaoEstilizado = styled.button<AbBotaoProps>`
    background: ${(props: AbBotaoProps) => props.tipo === 'primario' ? '#EB9B00' : '#FFF'};
    padding: 16px 32px;
    border: 2px solid #EB9B00;
    color: ${(props: AbBotaoProps) => props.tipo === 'primario' ? '#FFF' : '#EB9B00'};
    font-size: 20px;
    cursor: pointer;
    ${(props: AbBotaoProps) => props.tipo === 'primario' 
        ? css`
            &:hover {
                background: #B87900;
                border: 2px solid #B87900;
            }
        `
        : css`
        &:hover {
            background: #FFF;
            border: 2px solid #B87900;
            color: #B87900;
        }
        `
    }
`
export const AbBotao = ({ texto, onClick, tipo = 'primario' } : AbBotaoProps) => {
    return (<BotaoEstilizado onClick={onClick} tipo={tipo}>
        {texto}
    </BotaoEstilizado>)
}


import React, { useState } from "react";
import styled from "styled-components";

const SectionEstilizada = styled.section<{ selecionado: boolean }>`
    width: 194px;
    height: 88px;
    background: ${props => props.selecionado ? 'linear-gradient(97.54deg, #002F52 35.49%, #326589 165.37%)' : '#FFFFFF'};
    border: 1px solid;
    border-color: ${props => props.selecionado ? '#002F52' : '#EB9B00'};
    border-radius: 8px;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    text-align: center;
    margin: 10px;
    font-family: sans-serif;
    cursor: pointer;
    header {
        color: ${props => props.selecionado ? '#FFF' : '#EB9B00'};
        font-size: 12px;
        font-weight: 400;
    }
    strong {
        color: ${props => props.selecionado ? '#FFF' : '#EB9B00'};;
        font-weight: 700;
        font-size: 16px;
    }
    footer {
        color: ${props => props.selecionado ? '#FFF' : 'rgba(0, 0, 0, 0.54)'};;
        font-weight: 400;
        font-size: 12px;
    }
`

export interface AbGrupoOpcao {
    id: number
    titulo: string
    corpo: string
    rodape: string
}

export interface AbGrupoOpcoesProps {
    opcoes: AbGrupoOpcao[]
    valorPadrao?: AbGrupoOpcao | null
    onChange?: (opcao: AbGrupoOpcao) => void
}

export const AbGrupoOpcoes = ({ opcoes, onChange , valorPadrao} : AbGrupoOpcoesProps) => {
    const [selecao, setSelecao] = useState<AbGrupoOpcao | null>(valorPadrao ?? null)
    const aoSelecionar = (opcao: AbGrupoOpcao): void => {
        setSelecao(opcao);
        if (onChange) {
            onChange(opcao)
        }
    }

    return (<>
        {opcoes.map(opcao => 
            <SectionEstilizada
                onClick={() => aoSelecionar(opcao)}
                key={opcao.id}
                selecionado={selecao?.id == opcao.id}
            >
                <header>
                    {opcao.titulo}
                </header>
                <div>
                    <strong>{opcao.corpo}</strong>
                </div>
                <footer>
                    {opcao.rodape}
                </footer>
            </SectionEstilizada>
        )}
    </>)
}

import React, { ReactNode } from "react"
import styled from "styled-components";

export interface CardProps {
    children: ReactNode
}

const CardStyled = styled.div`
    padding: 48px;
    background: #FFFFFF;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 10px;
`

export const AbCard = ({ children }: CardProps) => {
    return (
        <CardStyled>
            {children}
        </CardStyled>
    )
}
import React from "react"
import styled from "styled-components";

export interface AbTagProps {
    texto: string
}

const TagStyled = styled.div`
    padding: 24px 32px;
    color: #FFFFFF;
    background: #EB9B00;
    font-weight: 700;
    font-size: 24px;
    display: inline-block;
    font-family: sans-serif;
`

export const AbTag = ({ texto }: AbTagProps) => {
    return (
        <TagStyled>
            {texto}
        </TagStyled>
    )
}
import React from "react";
import styled from "styled-components";

const LabelEstilizada = styled.label<{ darkmode: boolean }>`
    color: ${(props) => props.darkmode ? '#FFF' : '#002F52'};
    display: block;
    font-weight: 700;
    font-size: 16px;
    font-family: Arial, Helvetica, sans-serif;
    margin-left: 16px;
    margin-bottom: 8px
`

const InputEstilizado = styled.input<{ placeholderAlign: string, darkmode: boolean }>`
    font-size: 16px;
    line-height: 24px;
    padding: 8px 24px;
    border: 1px solid;
    border-radius: 45px;
    &:focus{
        outline: none;
    }
    width: 100%;
    box-sizing: border-box;
    background: ${(props) => props.darkmode ? 'transparent' : '#FFF'};
    border-color: ${(props) => props.darkmode ? '#FFF' : '#002F52'};
    text-align: ${(props) => props.placeholderAlign};
    ::placeholder,
    ::-webkit-input-placeholder  {
        color: ${(props) => props.darkmode ? '#FFF' : '#002F52'};
    }
`

export interface AbCampoTextoProps {
    label?: string;
    placeholder?: string;
    placeholderAlign?: 'left' | 'center' | 'right';
    value: string;
    type?: 'text' | 'email' | 'password' | 'date';
    onChange: (value: string) => void
    darkmode?: boolean
}

export const AbCampoTexto = ({ 
        label, 
        value, 
        onChange, 
        type = 'text', 
        placeholder = "", 
        placeholderAlign = 'left', 
        darkmode = false 
}: AbCampoTextoProps) => {
    return (
        <div>
            {label && <LabelEstilizada darkmode={darkmode}>
                {label}
            </LabelEstilizada>}
            <InputEstilizado
                placeholder={placeholder}
                placeholderAlign={placeholderAlign}
                darkmode={darkmode}
                type={type}
                value={value}
                onChange={event => onChange(event.target.value)}
            />
        </div>
    )
}
import React, { useEffect, useState } from "react"
import styled from "styled-components";

const BotaoEstilizado = styled.button`
    background: #002F52;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.25);
    border: none;
    cursor: pointer;
    color: #FFF;
    width: 28px;
    height: 28px;
    border-radius: 50%;
`
const SpanEstilizado = styled.span`
    font-size: 18px;
    line-height: 27px;
    display: inline-block;
    margin: 0 12px;
    color: #002F52;
    font-family: Arial, Helvetica, sans-serif;
`

const FlexContainerEstilizado = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
`
const ContainerEstilizado = styled.div`
    width: 100px;
    text-align: center;
    display: inline-block;
`

const LabelEstilizada = styled.label`
    color: #002F52;
    display: block;
    font-weight: 700;
    font-size: 16px;
    font-family: Arial, Helvetica, sans-serif;
    margin-bottom: 8px;
`

export interface AbInputQuantidadeProps {
    onChange?: (value:number) => void
}

export const AbInputQuantidade = ({ onChange } : AbInputQuantidadeProps) => {
    const [value, setValue] = useState(1)

    useEffect(() => {
        if (onChange) {            
            onChange(Number(value))
        }
    }, [value])

    return (
        <ContainerEstilizado>
            <LabelEstilizada>Quantidade</LabelEstilizada>
            <FlexContainerEstilizado>
                <BotaoEstilizado onClick={() => setValue(prev => prev - 1)}>
                    -
                </BotaoEstilizado>
                <SpanEstilizado>
                    {value}
                </SpanEstilizado>
                <BotaoEstilizado onClick={() => setValue(prev => prev + 1)}>
                    +
                </BotaoEstilizado>
            </FlexContainerEstilizado>
        </ContainerEstilizado>
    )
}
import React, { ReactNode } from "react"
import styled from "styled-components";

export interface AbModalProps {
    children: ReactNode,
    titulo: string,
    aberta: boolean,
    aoFechar: () => void
}

const JanelaModal = styled.div`
    position: fixed;
    padding: 64px;
    background: #FFFFFF;
    box-shadow: 4px 4px 24px -4px rgba(0, 0, 0, 0.15);
    border-radius: 24px;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`
const FundoModal = styled.div`
    position: fixed;
    left: 0px;
    right: 0px;
    top: 0px;
    bottom: 0px;
    background: rgba(101, 101, 101, 0.85);
`

const TituloModalWrapper = styled.div`
    display: flex;
    justify-content: space-between;
    font-family: sans-serif;
    align-items: center;
`
const TituloModal = styled.h2`
    font-weight: 700;
    font-size: 32px;
    line-height: 48px;
    color: #EB9B00;
    margin: 0;
`
const BotaoFecharModal = styled.button`
    background: transparent;
    border: none;
    cursor: pointer;
    color: #002F52;
`

export const AbModal = ({ children, aberta, aoFechar, titulo }: AbModalProps) => {
    if (!aberta) {
        return <></>
    }
    return (
        <>
            <FundoModal onClick={aoFechar} />
            <JanelaModal>
                <TituloModalWrapper>
                    <TituloModal>{titulo}</TituloModal>
                    <BotaoFecharModal onClick={aoFechar}>X</BotaoFecharModal>
                </TituloModalWrapper>
                {children}
            </JanelaModal>
        </>
    )
}
